        private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
        {
            Hashtable v_htb = new Hashtable();
            v_htb.Add(V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.SO_TIEN_L3, e_col_Number.SO_TIEN_L3);
            v_htb.Add(V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.SO_TIEN_L2, e_col_Number.SO_TIEN_L2);
            v_htb.Add(V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.NGAY_THU_L3, e_col_Number.NGAY_THU_L3);
            v_htb.Add(V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.TIEN_THUC_THU, e_col_Number.TIEN_THUC_THU);
            v_htb.Add(V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.NGAY_THU_L2, e_col_Number.NGAY_THU_L2);
            v_htb.Add(V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.HO_TEN, e_col_Number.HO_TEN);
            v_htb.Add(V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.CAN_PHAI_THU, e_col_Number.CAN_PHAI_THU);
            v_htb.Add(V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.NGAY_THU_L1, e_col_Number.NGAY_THU_L1);
            v_htb.Add(V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.CHENH_LECH, e_col_Number.CHENH_LECH);
            v_htb.Add(V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.TIEN_GIAM_TRU, e_col_Number.TIEN_GIAM_TRU);
            v_htb.Add(V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.TIEN_PHAI_THU, e_col_Number.TIEN_PHAI_THU);
            v_htb.Add(V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.SO_TIEN_L1, e_col_Number.SO_TIEN_L1);

            ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_ds.V_RPT_F496_BAO_CAO_SO_GIAO_DICH_THEO_LOP.NewRow());
            return v_obj_trans;
