            v_htb.Add(V_F500_BAO_CAO_LUY_KE_THEO_LOP_THEO_THANG.TIEN_THUC_THU_LUY_KE_THANG_2, e_col_Number.TIEN_THUC_THU_LUY_KE_THANG_2);
            v_htb.Add(V_F500_BAO_CAO_LUY_KE_THEO_LOP_THEO_THANG.TEN_LOP_MON, e_col_Number.TEN_LOP_MON);
            v_htb.Add(V_F500_BAO_CAO_LUY_KE_THEO_LOP_THEO_THANG.TIEN_THUC_THU_LUY_KE_THANG_4, e_col_Number.TIEN_THUC_THU_LUY_KE_THANG_4);
