            v_htb.Add(V_F500_BAO_CAO_LUY_KE_THEO_LOP_THEO_THANG.TIEN_THUC_THU, e_col_Number.TIEN_THUC_THU);
            v_htb.Add(V_F500_BAO_CAO_LUY_KE_THEO_LOP_THEO_THANG.TIEN_THUC_THU_LUY_KE_THANG_1, e_col_Number.TIEN_THUC_THU_LUY_KE_THANG_1);
            v_htb.Add(V_F500_BAO_CAO_LUY_KE_THEO_LOP_THEO_THANG.MA_LOP_MON, e_col_Number.MA_LOP_MON);
            v_htb.Add(V_F500_BAO_CAO_LUY_KE_THEO_LOP_THEO_THANG.TIEN_THUC_THU_LUY_KE_THANG_3, e_col_Number.TIEN_THUC_THU_LUY_KE_THANG_3);
            v_htb.Add(V_F500_BAO_CAO_LUY_KE_THEO_LOP_THEO_THANG.TIEN_THUC_THU_LUY_KE_THANG_5, e_col_Number.TIEN_THUC_THU_LUY_KE_THANG_5);
            v_htb.Add(V_F500_BAO_CAO_LUY_KE_THEO_LOP_THEO_THANG.TIEN_GIAM_TRU, e_col_Number.TIEN_GIAM_TRU);
            v_htb.Add(V_F500_BAO_CAO_LUY_KE_THEO_LOP_THEO_THANG.TIEN_PHAI_THU, e_col_Number.TIEN_PHAI_THU);

