            m_cmd_view.Click += new EventHandler(m_cmd_view_Click);
            m_cmd_search.Click += m_cmd_search_Click;
            this.KeyDown += f500_bao_cao_luy_ke_theo_lop_theo_thang_KeyDown;
        }

        void f500_bao_cao_luy_ke_theo_lop_theo_thang_KeyDown(object sender, KeyEventArgs e)
        {
            try
            {
                if (e.KeyCode == Keys.Enter)
                {
                    load_data_2_grid();
                }
            }
            catch (Exception v_e)
            {
                CSystemLog_301.ExceptionHandle(v_e);
            }
        }

        void m_cmd_search_Click(object sender, EventArgs e)
        {
            try
            {
                if (check_validate_data())
                {
                    load_data_2_grid();
                }
                else
                {
                    BaseMessages.MsgBox_Error("Bạn đã nhập sai rồi!!!");
                }
            }
            catch (Exception v_e)
            {
                CSystemLog_301.ExceptionHandle(v_e);
