            m_ds = new DS_V_HOC_SINH();
            m_ds.Clear();
            m_ds.EnforceConstraints = false;
            m_us.FillDataset_danh_sach_hoc_sinh(m_ds, m_txt_search.Text.Trim());
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            CGridUtils.MakeSoTT(0, m_fg);
            wrap_text_cell();
            m_fg.Redraw = true;
        }
