        void f220_cap_nhat_thong_tin_hoc_sinh_KeyDown(object sender, KeyEventArgs e)
        {
            try
            {             
                if ((e.KeyCode == Keys.Escape)&&(m_trang_thai_hien_thi == 1))
                {
                    this.Close();
                }
                else
                    if (e.KeyCode == Keys.Enter)
                    {
                        load_data_2_grid();
                    }
            }
            catch (Exception v_e)
            {
                CSystemLog_301.ExceptionHandle(v_e);
            } 
        }

